const doc = editor.state.doc;
const changes = [];
let inYaml = false;

for (let i = 1; i <= doc.lines; i++) {
  const line = doc.line(i);
  const text = line.text;
  const trimmed = text.trim();

  if (trimmed.startsWith('```yaml')) { inYaml = true; continue; }
  if (inYaml && trimmed.startsWith('```')) { inYaml = false; continue; }

  if (inYaml) {
    // 1. Handle "Key: Value"
    const colonIndex = text.indexOf(':');
    if (colonIndex !== -1) {
      const keyPart = text.substring(0, colonIndex + 1);
      changes.push({ from: line.from, to: line.to, insert: keyPart });
      continue;
    }

    // 2. Handle Lists "- Value"
    const dashIndex = text.indexOf('-');
    if (dashIndex !== -1 && text.substring(dashIndex).trim().startsWith('- ')) {
      const listPart = text.substring(0, dashIndex + 1) + ' ';
      changes.push({ from: line.from, to: line.to, insert: listPart });
    }
  }
}

if (changes.length > 0) {
  editor.dispatch({ changes });
}

const doc = editor.state.doc;
const pos = editor.state.selection.main.head;
const startLine = doc.lineAt(pos).number;
let recording = false;
const values = [];

for (let i = startLine; i <= doc.lines; i++) {
  const line = doc.line(i).text;
  const trimmed = line.trim();

  if (!recording && trimmed.startsWith('```yaml')) {
    recording = true;
    continue;
  }

  if (recording) {
    if (trimmed.startsWith('```')) break;
    if (!trimmed || trimmed.startsWith('#')) continue;

    const colonIndex = line.indexOf(':');
    if (colonIndex !== -1) {
      const val = line.substring(colonIndex + 1).trim();
      if (val) {
        values.push(val);
      }
    }
  }
}

if (values.length > 0) {
  navigator.clipboard.writeText(values.join('\n'));
}
