// Wrap selection in XML tags
const text = helpers.getSelection();
if (text) {
  helpers.replaceSelection(`<context>
${text}
</context>`);
} else {
  helpers.insertAtCursor('<context>

</context>');
}

// Wrap selection in triple quotes
const text = helpers.getSelection();
helpers.replaceSelection(`"""
${text}
"""`);

// Remove common conversational filler from AI responses
const text = helpers.getSelection() || helpers.getAllText();

// Regex for common headers/footers
const cleaners = [
  /^Sure, here is.*/im,
  /^Certainly!.*/im,
  /^Here's an example.*/im,
  /^I hope this helps.*/im,
  /^Let me know if.*/im
];

let cleaned = text;
cleaners.forEach(regex => {
  cleaned = cleaned.replace(regex, '');
});

// Trim extra whitespace
cleaned = cleaned.trim();

if (helpers.getSelection()) {
  helpers.replaceSelection(cleaned);
} else {
  helpers.replaceAllText(cleaned);
}

// Format selection as a User message
const text = helpers.getSelection();
helpers.replaceSelection(`**User:**
${text}

---
`);

// Format selection as an Assistant message
const text = helpers.getSelection();
helpers.replaceSelection(`**Assistant:**
${text}

---
`);

// Remove multiple newlines and trim lines
const text = helpers.getSelection();
// Replace 3+ newlines with 2, trim lines
const minified = text.split('
').map(l => l.trim()).filter(l => l).join('
');
helpers.replaceSelection(minified);

// Extract only content inside ``` blocks
const text = helpers.getSelection();
const regex = /```[\s\S]*?```/g;
const matches = text.match(regex);

if (matches) {
  helpers.replaceSelection(matches.join('

'));
} else {
  // No code blocks found
  // alert('No code blocks found'); // Alert not available in sandbox, maybe just do nothing
}
